import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import FingerprintScanner from '../components/FingerprintScanner';
import FaceCapture from '../components/FaceCapture';
import { 
  User, 
  Mail, 
  Phone, 
  Fingerprint, 
  AlertCircle,
  Loader,
  CheckCircle
} from 'lucide-react';

const Register = () => {
  const [userType, setUserType] = useState('voter');
  const [formData, setFormData] = useState({
    voterId: '',
    name: '',
    email: '',
    phone: '',
    fingerprintData: null,
    faceData: null
  });
  const [useFace, setUseFace] = useState(false);
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [registrationSuccess, setRegistrationSuccess] = useState(false);

  const { register } = useAuth();
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleFingerprintScan = (fingerprintData) => {
    setFormData(prev => ({
      ...prev,
      fingerprintData
    }));
  };

  const handleFaceCapture = (faceData) => {
    setFormData(prev => ({
      ...prev,
      faceData
    }));
  };

  const handleFingerprintError = (error) => {
    console.error('Fingerprint error:', error);
    setErrors(prev => ({
      ...prev,
      fingerprint: 'Failed to capture fingerprint. Please try again.'
    }));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.voterId.trim()) {
      newErrors.voterId = 'Voter ID is required';
    } else if (formData.voterId.length < 3) {
      newErrors.voterId = 'Voter ID must be at least 3 characters';
    }

    if (!formData.name.trim()) {
      newErrors.name = 'Full name is required';
    } else if (formData.name.length < 2) {
      newErrors.name = 'Name must be at least 2 characters';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = 'Phone number is required';
    } else if (!/^[\+]?[1-9][\d]{0,15}$/.test(formData.phone.replace(/\s/g, ''))) {
      newErrors.phone = 'Please enter a valid phone number';
    }

    if (!useFace && !formData.fingerprintData) {
      newErrors.fingerprint = 'Fingerprint scan is required (or switch to Face ID)';
    }
    if (useFace && !formData.faceData) {
      newErrors.face = 'Face capture is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);
    setErrors({});

    try {
      const result = await register({
        ...formData,
        fingerprintData: useFace ? undefined : formData.fingerprintData,
        faceData: useFace ? formData.faceData : undefined
      }, userType);
      
      if (result.success) {
        setRegistrationSuccess(true);
      }
    } catch (error) {
      console.error('Registration error:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetForm = () => {
    setFormData({
      voterId: '',
      name: '',
      email: '',
      phone: '',
      fingerprintData: null
    });
    setErrors({});
    setRegistrationSuccess(false);
  };

  if (registrationSuccess) {
    return (
      <div className="min-h-screen flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-md w-full">
          <div className="card text-center">
            <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <CheckCircle className="w-8 h-8 text-green-600" />
            </div>
            
            <h2 className="text-2xl font-bold text-gray-900 mb-2">
              Registration Successful!
            </h2>
            
            <p className="text-gray-600 mb-6">
              Your voter registration has been submitted successfully. 
              Please wait for admin verification before you can vote.
            </p>
            
            <div className="space-y-3">
              <Link to="/login" className="btn btn-primary w-full">
                Go to Login
              </Link>
              <Link to="/" className="btn btn-secondary w-full">
                Back to Home
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div className="text-center">
          <h2 className="text-3xl font-bold text-white mb-2">
            Create Account
          </h2>
          <p className="text-white/80">
            Register as a voter to participate in elections
          </p>
        </div>

        <div className="card">
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Voter ID Input */}
            <div className="form-group">
              <label htmlFor="voterId" className="form-label">
                Voter ID *
              </label>
              <div className="relative">
                <input
                  id="voterId"
                  name="voterId"
                  type="text"
                  value={formData.voterId}
                  onChange={handleInputChange}
                  className={`form-input ${errors.voterId ? 'border-red-500' : ''}`}
                  placeholder="Enter your unique voter ID"
                />
                <User className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              </div>
              {errors.voterId && (
                <p className="mt-1 text-sm text-red-600 flex items-center">
                  <AlertCircle className="w-4 h-4 mr-1" />
                  {errors.voterId}
                </p>
              )}
            </div>

            {/* Full Name Input */}
            <div className="form-group">
              <label htmlFor="name" className="form-label">
                Full Name *
              </label>
              <div className="relative">
                <input
                  id="name"
                  name="name"
                  type="text"
                  value={formData.name}
                  onChange={handleInputChange}
                  className={`form-input ${errors.name ? 'border-red-500' : ''}`}
                  placeholder="Enter your full name"
                />
                <User className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              </div>
              {errors.name && (
                <p className="mt-1 text-sm text-red-600 flex items-center">
                  <AlertCircle className="w-4 h-4 mr-1" />
                  {errors.name}
                </p>
              )}
            </div>

            {/* Email Input */}
            <div className="form-group">
              <label htmlFor="email" className="form-label">
                Email Address *
              </label>
              <div className="relative">
                <input
                  id="email"
                  name="email"
                  type="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className={`form-input ${errors.email ? 'border-red-500' : ''}`}
                  placeholder="Enter your email address"
                />
                <Mail className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              </div>
              {errors.email && (
                <p className="mt-1 text-sm text-red-600 flex items-center">
                  <AlertCircle className="w-4 h-4 mr-1" />
                  {errors.email}
                </p>
              )}
            </div>

            {/* Phone Input */}
            <div className="form-group">
              <label htmlFor="phone" className="form-label">
                Phone Number *
              </label>
              <div className="relative">
                <input
                  id="phone"
                  name="phone"
                  type="tel"
                  value={formData.phone}
                  onChange={handleInputChange}
                  className={`form-input ${errors.phone ? 'border-red-500' : ''}`}
                  placeholder="Enter your phone number"
                />
                <Phone className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              </div>
              {errors.phone && (
                <p className="mt-1 text-sm text-red-600 flex items-center">
                  <AlertCircle className="w-4 h-4 mr-1" />
                  {errors.phone}
                </p>
              )}
            </div>

            {/* Biometric Method Toggle */}
            <div className="flex bg-gray-100 rounded-xl p-1 mb-4 gap-2">
              <button type="button" onClick={() => setUseFace(false)} className={`flex-1 py-2 rounded-lg text-sm font-semibold ${!useFace ? 'bg-white shadow' : ''}`}>Fingerprint</button>
              <button type="button" onClick={() => setUseFace(true)} className={`flex-1 py-2 rounded-lg text-sm font-semibold ${useFace ? 'bg-white shadow' : ''}`}>Face ID</button>
            </div>

            {/* Fingerprint Scanner / Face Capture */}
            <div className="form-group">
              <label className="form-label">{useFace ? 'Face ID Registration *' : 'Fingerprint Registration *'}</label>
              {useFace ? (
                <FaceCapture onCapture={handleFaceCapture} onError={handleFingerprintError} />
              ) : (
                <FingerprintScanner
                  onScan={handleFingerprintScan}
                  onError={handleFingerprintError}
                  isScanning={false}
                  disabled={false}
                />
              )}
              {(!useFace && errors.fingerprint) && (
                <p className="mt-1 text-sm text-red-600 flex items-center">
                  <AlertCircle className="w-4 h-4 mr-1" />
                  {errors.fingerprint}
                </p>
              )}
              {(useFace && errors.face) && (
                <p className="mt-1 text-sm text-red-600 flex items-center">
                  <AlertCircle className="w-4 h-4 mr-1" />
                  {errors.face}
                </p>
              )}
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              disabled={isSubmitting}
              className="btn btn-primary w-full"
            >
              {isSubmitting ? (
                <>
                  <Loader className="w-5 h-5 animate-spin" />
                  Registering...
                </>
              ) : (
                <>
                  <Fingerprint className="w-5 h-5" />
                  Register as Voter
                </>
              )}
            </button>
          </form>

          {/* Links */}
          <div className="mt-6 text-center space-y-2">
            <p className="text-sm text-gray-600">
              Already have an account?{' '}
              <Link to="/login" className="text-blue-600 hover:text-blue-800 font-medium">
                Sign in here
              </Link>
            </p>
            <p className="text-sm text-gray-600">
              <Link to="/" className="text-blue-600 hover:text-blue-800">
                ← Back to Home
              </Link>
            </p>
          </div>
        </div>

        {/* Privacy Notice */}
        <div className="text-center">
          <div className="inline-flex items-center space-x-2 text-sm text-white/70">
            <Fingerprint className="w-4 h-4" />
            <span>Your fingerprint data is encrypted and stored securely</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
