import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Results = () => {
  const { electionId } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();
  const [election, setElection] = useState(null);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }

    fetchResults();
  }, [electionId, user, navigate]);

  const fetchResults = async () => {
    try {
      const response = await fetch(`/api/elections/${electionId}/results`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setElection(data.election);
        setResults(data.results);
      } else {
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Error fetching results:', error);
      navigate('/dashboard');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  if (!election) {
    return (
      <div className="container mt-4">
        <div className="alert alert-warning">
          Election not found.
        </div>
        <button className="btn btn-primary" onClick={() => navigate('/dashboard')}>
          Back to Dashboard
        </button>
      </div>
    );
  }

  const totalVotes = results.reduce((sum, result) => sum + result.voteCount, 0);

  return (
    <div className="modern-container py-8">
      {/* Header */}
      <div className="glass-card p-8 mb-8 animate-fadeInUp">
        <div className="text-center">
          <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-r from-green-500 to-blue-600 rounded-full mb-4">
            <svg className="w-10 h-10 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
            </svg>
          </div>
          <h1 className="text-4xl font-bold text-gradient mb-4">Election Results</h1>
          <h2 className="text-2xl font-semibold text-gray-700 mb-2">{election.title}</h2>
          <p className="text-xl text-gray-600 mb-6">{election.description}</p>
          <div className="flex items-center justify-center space-x-4 text-sm text-gray-500">
            <div className="flex items-center">
              <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              <span>Live Updates</span>
            </div>
            <div className="flex items-center">
              <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
              </svg>
              <span>Verified Results</span>
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-2 gap-8">
        {/* Results Section */}
        <div className="glass-card p-8 animate-fadeInUp">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-2xl font-bold text-gradient">Vote Results</h2>
            <div className="flex items-center space-x-2 text-sm text-gray-600">
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
              <span>Live</span>
            </div>
          </div>

          {results.length === 0 ? (
            <div className="text-center py-12">
              <div className="inline-flex items-center justify-center w-16 h-16 bg-gray-100 rounded-full mb-4">
                <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
              </div>
              <h3 className="text-xl font-semibold text-gray-600 mb-2">No Votes Cast Yet</h3>
              <p className="text-gray-500">Results will appear here once voting begins.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {results.map((result, index) => {
                const percentage = totalVotes > 0 ? (result.voteCount / totalVotes) * 100 : 0;
                return (
                  <div key={result.candidateId} className="card-modern hover-lift animate-fadeInUp" style={{animationDelay: `${index * 0.1}s`}}>
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-4">
                        <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center">
                          <span className="text-white font-bold text-lg">
                            {result.candidateName.charAt(0)}
                          </span>
                        </div>
                        <div>
                          <h3 className="text-lg font-bold text-gray-800">{result.candidateName}</h3>
                          <p className="text-blue-600 font-medium">{result.party}</p>
                        </div>
                      </div>
                      
                      <div className="text-right">
                        <div className="text-2xl font-bold text-gray-800 mb-2">{result.voteCount} votes</div>
                        <div className="w-32">
                          <div className="progress-modern">
                            <div 
                              className="progress-bar" 
                              style={{width: `${percentage}%`}}
                            ></div>
                          </div>
                          <div className="text-sm text-gray-500 mt-1">
                            {percentage.toFixed(1)}%
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>

        {/* Statistics Section */}
        <div className="space-y-6">
          {/* Election Stats */}
          <div className="glass-card p-8 animate-fadeInUp">
            <h3 className="text-xl font-bold text-gradient mb-6">Election Statistics</h3>
            <div className="grid grid-2 gap-6">
              <div className="stat-card">
                <div className="stat-number">{totalVotes}</div>
                <div className="stat-label">Total Votes</div>
              </div>
              <div className="stat-card">
                <div className="stat-number">{election.registeredVoters}</div>
                <div className="stat-label">Registered Voters</div>
              </div>
            </div>
            
            <div className="mt-6">
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm font-medium text-gray-600">Voter Turnout</span>
                <span className="text-sm font-bold text-gray-800">
                  {((totalVotes / election.registeredVoters) * 100).toFixed(1)}%
                </span>
              </div>
              <div className="progress-modern">
                <div 
                  className="progress-bar" 
                  style={{width: `${(totalVotes / election.registeredVoters) * 100}%`}}
                ></div>
              </div>
            </div>
          </div>

          {/* Election Info */}
          <div className="glass-card p-8 animate-fadeInUp">
            <h3 className="text-xl font-bold text-gradient mb-6">Election Details</h3>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <span className="text-gray-600">Status:</span>
                <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                  election.status === 'active' 
                    ? 'bg-green-100 text-green-800' 
                    : election.status === 'completed'
                    ? 'bg-gray-100 text-gray-800'
                    : 'bg-yellow-100 text-yellow-800'
                }`}>
                  {election.status}
                </span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-gray-600">Start Date:</span>
                <span className="font-medium">{new Date(election.startDate).toLocaleDateString()}</span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-gray-600">End Date:</span>
                <span className="font-medium">{new Date(election.endDate).toLocaleDateString()}</span>
              </div>
            </div>
          </div>

          {/* Back Button */}
          <div className="glass-card p-8 animate-fadeInUp">
            <button 
              className="btn-modern btn-primary w-full py-3"
              onClick={() => navigate('/dashboard')}
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
              </svg>
              Back to Dashboard
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Results;
