import React, { useEffect, useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useSocket } from '../contexts/SocketContext';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const { user } = useAuth();
  const { socket } = useSocket();
  const navigate = useNavigate();
  const [elections, setElections] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }

    // Fetch user's elections
    fetchElections();
  }, [user, navigate]);

  const fetchElections = async () => {
    try {
      const response = await fetch('/api/elections', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setElections(data);
      }
    } catch (error) {
      console.error('Error fetching elections:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleVote = (electionId) => {
    navigate(`/vote/${electionId}`);
  };

  const handleViewResults = (electionId) => {
    navigate(`/results/${electionId}`);
  };

  if (loading) {
    return (
      <div className="container">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-md-12">
          <h2>Welcome, {user?.name || user?.email}</h2>
          <p className="text-muted">Your Voting Dashboard</p>
        </div>
      </div>

      <div className="row mt-4">
        <div className="col-md-12">
          <h4>Available Elections</h4>
          {elections.length === 0 ? (
            <div className="alert alert-info">
              No elections available at the moment.
            </div>
          ) : (
            <div className="row">
              {elections.map((election) => (
                <div key={election._id} className="col-md-6 mb-3">
                  <div className="card">
                    <div className="card-body">
                      <h5 className="card-title">{election.title}</h5>
                      <p className="card-text">{election.description}</p>
                      <p className="card-text">
                        <small className="text-muted">
                          Status: {election.status}
                        </small>
                      </p>
                      <div className="d-flex gap-2">
                        {election.status === 'active' && (
                          <button
                            className="btn btn-primary"
                            onClick={() => handleVote(election._id)}
                          >
                            Vote Now
                          </button>
                        )}
                        <button
                          className="btn btn-outline-secondary"
                          onClick={() => handleViewResults(election._id)}
                        >
                          View Results
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
