import React, { useContext, useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Results = () => {
  const { electionId } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();
  const [election, setElection] = useState(null);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }

    fetchResults();
  }, [electionId, user, navigate]);

  const fetchResults = async () => {
    try {
      const response = await fetch(`/api/elections/${electionId}/results`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setElection(data.election);
        setResults(data.results);
      } else {
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Error fetching results:', error);
      navigate('/dashboard');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  if (!election) {
    return (
      <div className="container mt-4">
        <div className="alert alert-warning">
          Election not found.
        </div>
        <button className="btn btn-primary" onClick={() => navigate('/dashboard')}>
          Back to Dashboard
        </button>
      </div>
    );
  }

  const totalVotes = results.reduce((sum, result) => sum + result.voteCount, 0);

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-md-10">
          <div className="card">
            <div className="card-header">
              <h4>Election Results: {election.title}</h4>
              <p className="mb-0">{election.description}</p>
            </div>
            <div className="card-body">
              <div className="row mb-4">
                <div className="col-md-6">
                  <div className="card bg-light">
                    <div className="card-body text-center">
                      <h5>Total Votes</h5>
                      <h3 className="text-primary">{totalVotes}</h3>
                    </div>
                  </div>
                </div>
                <div className="col-md-6">
                  <div className="card bg-light">
                    <div className="card-body text-center">
                      <h5>Status</h5>
                      <h3 className={`${election.status === 'active' ? 'text-success' : 'text-secondary'}`}>
                        {election.status}
                      </h3>
                    </div>
                  </div>
                </div>
              </div>

              <h5>Results by Candidate:</h5>
              <div className="row">
                {results.map((result, index) => {
                  const percentage = totalVotes > 0 ? (result.voteCount / totalVotes) * 100 : 0;
                  return (
                    <div key={result.candidateId} className="col-md-6 mb-3">
                      <div className="card">
                        <div className="card-body">
                          <div className="d-flex justify-content-between align-items-center mb-2">
                            <h6 className="mb-0">{result.candidateName}</h6>
                            <span className="badge bg-primary">{result.voteCount} votes</span>
                          </div>
                          <div className="progress mb-2">
                            <div
                              className="progress-bar"
                              role="progressbar"
                              style={{ width: `${percentage}%` }}
                              aria-valuenow={percentage}
                              aria-valuemin="0"
                              aria-valuemax="100"
                            >
                              {percentage.toFixed(1)}%
                            </div>
                          </div>
                          <small className="text-muted">
                            {result.party} â€¢ {percentage.toFixed(1)}% of total votes
                          </small>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>

              {results.length === 0 && (
                <div className="alert alert-info">
                  No votes have been cast yet.
                </div>
              )}

              <div className="mt-4">
                <button className="btn btn-primary" onClick={() => navigate('/dashboard')}>
                  Back to Dashboard
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Results;
