import React from 'react';
import { useAuth } from '../contexts/AuthContext';

const DashboardLayout = ({ children, title, subtitle, actions, sidebar, stats }) => {
  const { user } = useAuth();

  // Safe user data extraction
  const getUserInitial = () => {
    if (user?.name && user.name.length > 0) return user.name.charAt(0).toUpperCase();
    if (user?.email && user.email.length > 0) return user.email.charAt(0).toUpperCase();
    if (user?.username && user.username.length > 0) return user.username.charAt(0).toUpperCase();
    return 'U';
  };

  const getUserDisplayName = () => {
    return user?.name || user?.username || user?.email || 'User';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-saffron-50 to-india-green-50">
      {/* Header */}
      <header className="bg-white border-b border-neutral-200 shadow-lg sticky top-0 z-40">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="w-12 h-12 bg-gradient-to-r from-saffron-500 to-chakra-blue-500 rounded-xl flex items-center justify-center shadow-lg">
                <svg className="w-7 h-7 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <div>
                <h1 className="text-2xl font-bold text-neutral-900">भारत वोट</h1>
                <p className="text-sm text-neutral-600 font-medium">Secure Digital Voting Platform</p>
              </div>
            </div>
            
                         <div className="flex items-center space-x-4">
               {user && (
                 <div className="flex items-center space-x-3">
                   <div className="w-10 h-10 bg-gradient-to-r from-saffron-500 to-india-green-500 rounded-full flex items-center justify-center shadow-lg">
                     <span className="text-white font-bold text-sm">
                       {getUserInitial()}
                     </span>
                   </div>
                   <div className="hidden md:block">
                     <p className="text-sm font-semibold text-neutral-900">{getUserDisplayName()}</p>
                     <p className="text-xs text-neutral-600 font-medium">{user.userType || 'User'}</p>
                   </div>
                 </div>
               )}
             </div>
          </div>
        </div>
      </header>

      <div className="container mx-auto px-4 py-8">
        {/* Page Header */}
        {(title || subtitle || actions) && (
          <div className="mb-8">
            <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
              <div>
                {title && <h1 className="text-h1 text-neutral-900 mb-2">{title}</h1>}
                {subtitle && <p className="text-body-lg text-neutral-600">{subtitle}</p>}
              </div>
              {actions && (
                <div className="flex flex-wrap gap-3">
                  {actions}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Stats Section */}
        {stats && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            {stats.map((stat, index) => (
              <div 
                key={index} 
                className="stat-card animate-fadeInUp"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-white/80 text-sm font-medium mb-1">{stat.label}</p>
                    <p className="stat-number">{stat.value}</p>
                  </div>
                  {stat.icon && (
                    <div className="w-12 h-12 bg-white/20 rounded-full flex items-center justify-center">
                      {stat.icon}
                    </div>
                  )}
                </div>
                {stat.change && (
                  <div className="mt-4 flex items-center text-sm">
                    <span className={`mr-2 ${stat.change > 0 ? 'text-green-200' : 'text-red-200'}`}>
                      {stat.change > 0 ? '↗' : '↘'}
                    </span>
                    <span className="text-white/80">
                      {Math.abs(stat.change)}% from last month
                    </span>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {/* Main Content */}
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Sidebar */}
          {sidebar && (
            <aside className="lg:col-span-1">
              <div className="sticky top-24">
                {sidebar}
              </div>
            </aside>
          )}

          {/* Main Content Area */}
          <main className={`${sidebar ? 'lg:col-span-3' : 'lg:col-span-4'}`}>
            {children}
          </main>
        </div>
      </div>
    </div>
  );
};

export default DashboardLayout;
