import React, { useContext, useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import FingerprintScanner from '../components/FingerprintScanner';

const Vote = () => {
  const { electionId } = useParams();
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [election, setElection] = useState(null);
  const [candidates, setCandidates] = useState([]);
  const [selectedCandidate, setSelectedCandidate] = useState('');
  const [fingerprintVerified, setFingerprintVerified] = useState(false);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }

    fetchElectionDetails();
  }, [electionId, user, navigate]);

  const fetchElectionDetails = async () => {
    try {
      const response = await fetch(`/api/elections/${electionId}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setElection(data.election);
        setCandidates(data.candidates);
      } else {
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Error fetching election details:', error);
      navigate('/dashboard');
    } finally {
      setLoading(false);
    }
  };

  const handleFingerprintVerification = (verified) => {
    setFingerprintVerified(verified);
  };

  const handleVoteSubmit = async () => {
    if (!selectedCandidate || !fingerprintVerified) {
      alert('Please select a candidate and verify your fingerprint.');
      return;
    }

    setSubmitting(true);

    try {
      const response = await fetch('/api/votes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          electionId,
          candidateId: selectedCandidate
        })
      });

      if (response.ok) {
        alert('Vote submitted successfully!');
        navigate('/dashboard');
      } else {
        const error = await response.json();
        alert(`Error: ${error.message}`);
      }
    } catch (error) {
      console.error('Error submitting vote:', error);
      alert('Error submitting vote. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  if (!election || election.status !== 'active') {
    return (
      <div className="container mt-4">
        <div className="alert alert-warning">
          This election is not currently active.
        </div>
        <button className="btn btn-primary" onClick={() => navigate('/dashboard')}>
          Back to Dashboard
        </button>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header">
              <h4>{election.title}</h4>
              <p className="mb-0">{election.description}</p>
            </div>
            <div className="card-body">
              <h5>Select Your Candidate:</h5>
              <div className="row">
                {candidates.map((candidate) => (
                  <div key={candidate._id} className="col-md-6 mb-3">
                    <div 
                      className={`card candidate-card ${selectedCandidate === candidate._id ? 'border-primary' : ''}`}
                      style={{ cursor: 'pointer' }}
                      onClick={() => setSelectedCandidate(candidate._id)}
                    >
                      <div className="card-body text-center">
                        <h6>{candidate.name}</h6>
                        <p className="text-muted">{candidate.party}</p>
                        {candidate.description && (
                          <p className="small">{candidate.description}</p>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              <div className="mt-4">
                <h5>Fingerprint Verification:</h5>
                <FingerprintScanner onVerification={handleFingerprintVerification} />
                {fingerprintVerified && (
                  <div className="alert alert-success mt-2">
                    ✓ Fingerprint verified successfully
                  </div>
                )}
              </div>

              <div className="mt-4 d-flex gap-2">
                <button
                  className="btn btn-primary"
                  onClick={handleVoteSubmit}
                  disabled={!selectedCandidate || !fingerprintVerified || submitting}
                >
                  {submitting ? 'Submitting...' : 'Submit Vote'}
                </button>
                <button
                  className="btn btn-secondary"
                  onClick={() => navigate('/dashboard')}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Vote;
