import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import FingerprintScanner from '../components/FingerprintScanner';

const Vote = () => {
  const { electionId } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();
  const [election, setElection] = useState(null);
  const [candidates, setCandidates] = useState([]);
  const [selectedCandidate, setSelectedCandidate] = useState('');
  const [fingerprintVerified, setFingerprintVerified] = useState(false);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }

    fetchElectionDetails();
  }, [electionId, user, navigate]);

  const fetchElectionDetails = async () => {
    try {
      const response = await fetch(`/api/elections/${electionId}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setElection(data.election);
        setCandidates(data.candidates);
      } else {
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Error fetching election details:', error);
      navigate('/dashboard');
    } finally {
      setLoading(false);
    }
  };

  const handleFingerprintVerification = (verified) => {
    setFingerprintVerified(verified);
  };

  const handleVoteSubmit = async () => {
    if (!selectedCandidate || !fingerprintVerified) {
      alert('Please select a candidate and verify your fingerprint.');
      return;
    }

    setSubmitting(true);

    try {
      const response = await fetch('/api/votes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          electionId,
          candidateId: selectedCandidate
        })
      });

      if (response.ok) {
        alert('Vote submitted successfully!');
        navigate('/dashboard');
      } else {
        const error = await response.json();
        alert(`Error: ${error.message}`);
      }
    } catch (error) {
      console.error('Error submitting vote:', error);
      alert('Error submitting vote. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  if (!election || election.status !== 'active') {
    return (
      <div className="container mt-4">
        <div className="alert alert-warning">
          This election is not currently active.
        </div>
        <button className="btn btn-primary" onClick={() => navigate('/dashboard')}>
          Back to Dashboard
        </button>
      </div>
    );
  }

  return (
    <div className="modern-container py-8">
      <div className="max-w-4xl mx-auto">
        {/* Election Header */}
        <div className="glass-card p-8 mb-8 animate-fadeInUp">
          <div className="text-center">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-green-500 to-blue-600 rounded-full mb-4">
              <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <h1 className="text-4xl font-bold text-gradient mb-4">{election.title}</h1>
            <p className="text-xl text-gray-600 mb-6">{election.description}</p>
            <div className="flex items-center justify-center space-x-4 text-sm text-gray-500">
              <div className="flex items-center">
                <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <span>Ends: {new Date(election.endDate).toLocaleDateString()}</span>
              </div>
              <div className="flex items-center">
                <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
                </svg>
                <span>Secure Voting</span>
              </div>
            </div>
          </div>
        </div>

        {/* Candidate Selection */}
        <div className="glass-card p-8 mb-8 animate-fadeInUp">
          <h2 className="text-2xl font-bold text-gradient mb-6 text-center">Select Your Candidate</h2>
          <div className="grid grid-2 gap-6">
            {candidates.map((candidate, index) => (
              <div 
                key={candidate._id} 
                className={`candidate-card ${selectedCandidate === candidate._id ? 'selected' : ''}`}
                onClick={() => setSelectedCandidate(candidate._id)}
                style={{animationDelay: `${index * 0.1}s`}}
              >
                <div className="text-center">
                  <div className="w-16 h-16 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center mx-auto mb-4">
                    <span className="text-2xl font-bold text-white">
                      {candidate.name.charAt(0)}
                    </span>
                  </div>
                  <h3 className="text-xl font-bold text-gray-800 mb-2">{candidate.name}</h3>
                  <p className="text-blue-600 font-semibold mb-2">{candidate.party}</p>
                  {candidate.description && (
                    <p className="text-gray-600 text-sm">{candidate.description}</p>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Fingerprint Verification */}
        <div className="glass-card p-8 mb-8 animate-fadeInUp">
          <h2 className="text-2xl font-bold text-gradient mb-6 text-center">Fingerprint Verification</h2>
          <div className="max-w-md mx-auto">
            <FingerprintScanner onVerification={handleFingerprintVerification} />
            {fingerprintVerified && (
              <div className="mt-4 p-4 bg-green-50 border border-green-200 rounded-lg flex items-center">
                <svg className="w-6 h-6 text-green-600 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <span className="text-green-800 font-medium">Fingerprint verified successfully!</span>
              </div>
            )}
          </div>
        </div>

        {/* Action Buttons */}
        <div className="glass-card p-8 animate-fadeInUp">
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <button
              className="btn-modern btn-primary px-8 py-4 text-lg font-semibold"
              onClick={handleVoteSubmit}
              disabled={!selectedCandidate || !fingerprintVerified || submitting}
            >
              {submitting ? (
                <div className="flex items-center">
                  <div className="spinner-modern mr-3"></div>
                  Submitting Vote...
                </div>
              ) : (
                <div className="flex items-center">
                  <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  Submit Vote
                </div>
              )}
            </button>
            <button
              className="btn-modern btn-outline px-8 py-4 text-lg font-semibold"
              onClick={() => navigate('/dashboard')}
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
              Cancel
            </button>
          </div>
          
          {/* Voting Status */}
          <div className="mt-6 text-center">
            <div className="flex items-center justify-center space-x-6 text-sm text-gray-600">
              <div className={`flex items-center ${selectedCandidate ? 'text-green-600' : 'text-gray-400'}`}>
                <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <span>Candidate Selected</span>
              </div>
              <div className={`flex items-center ${fingerprintVerified ? 'text-green-600' : 'text-gray-400'}`}>
                <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
                </svg>
                <span>Fingerprint Verified</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Vote;
