import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useSocket } from '../contexts/SocketContext';
import DashboardLayout from '../components/DashboardLayout';
import Chart from '../components/Chart';

const Dashboard = () => {
  const { user } = useAuth();
  const { socket } = useSocket();
  const navigate = useNavigate();
  const [elections, setElections] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate fetching elections data
    setTimeout(() => {
      setElections([
        {
          _id: '1',
          title: 'Presidential Election 2024',
          description: 'National presidential election for the year 2024',
          status: 'active',
          startDate: '2024-01-01',
          endDate: '2024-12-31',
          registeredVoters: 150000,
          totalVotes: 120000
        },
        {
          _id: '2',
          title: 'Local Council Election',
          description: 'Election for local council members',
          status: 'upcoming',
          startDate: '2024-06-01',
          endDate: '2024-06-30',
          registeredVoters: 50000,
          totalVotes: 0
        },
        {
          _id: '3',
          title: 'Student Union Election',
          description: 'University student union leadership election',
          status: 'completed',
          startDate: '2024-03-01',
          endDate: '2024-03-15',
          registeredVoters: 25000,
          totalVotes: 22000
        }
      ]);
      setLoading(false);
    }, 1000);
  }, []);

  const handleVote = (electionId) => {
    navigate(`/vote/${electionId}`);
  };

  const handleViewResults = (electionId) => {
    navigate(`/results/${electionId}`);
  };

  const showNotification = (message, type = 'info') => {
    // Create a temporary notification element
    const notification = document.createElement('div');
    notification.className = `fixed top-4 right-4 z-50 p-4 rounded-lg shadow-lg text-white max-w-sm ${
      type === 'error' ? 'bg-red-500' : 'bg-blue-500'
    }`;
    notification.textContent = message;
    
    // Add to DOM
    document.body.appendChild(notification);
    
    // Auto remove after 4 seconds
    setTimeout(() => {
      if (notification.parentNode) {
        notification.parentNode.removeChild(notification);
      }
    }, 4000);
  };

  const handleCreateElection = () => {
    // Check if user is admin, if not show message
    if (user?.role === 'admin' || user?.role === 'super_admin') {
      navigate('/admin/elections');
    } else {
      showNotification('Only administrators can create elections. Please contact your system administrator.', 'error');
    }
  };

  const handleViewReports = () => {
    // Check if user is admin, if not show message
    if (user?.role === 'admin' || user?.role === 'super_admin') {
      navigate('/admin/dashboard');
    } else {
      showNotification('Only administrators can view reports. Please contact your system administrator.', 'error');
    }
  };

  const handleSettings = () => {
    navigate('/profile');
  };

  // Dashboard statistics
  const stats = [
    {
      label: 'Active Elections',
      value: elections.filter(e => e.status === 'active').length,
      icon: (
        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      ),
      change: 12
    },
    {
      label: 'Total Votes Cast',
      value: elections.reduce((sum, e) => sum + e.totalVotes, 0).toLocaleString(),
      icon: (
        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
        </svg>
      ),
      change: 8
    },
    {
      label: 'Registered Voters',
      value: elections.reduce((sum, e) => sum + e.registeredVoters, 0).toLocaleString(),
      icon: (
        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197m13.5-9a2.5 2.5 0 11-5 0 2.5 2.5 0 015 0z" />
        </svg>
      ),
      change: 15
    },
    {
      label: 'Voter Turnout',
      value: `${Math.round((elections.reduce((sum, e) => sum + e.totalVotes, 0) / elections.reduce((sum, e) => sum + e.registeredVoters, 0)) * 100)}%`,
      icon: (
        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
        </svg>
      ),
      change: 5
    }
  ];

  // Chart data
  const chartData = {
    voterTurnout: {
      data: elections.map(e => Math.round((e.totalVotes / e.registeredVoters) * 100)),
      labels: elections.map(e => e.title.split(' ')[0])
    },
    votesByElection: {
      data: elections.map(e => e.totalVotes),
      labels: elections.map(e => e.title.split(' ')[0])
    }
  };

  // Sidebar navigation
  const sidebar = (
    <div className="space-y-4">
      <div className="card">
        <h3 className="text-h4 text-neutral-900 mb-4">Quick Actions</h3>
        <div className="space-y-3">
          <button 
            className="btn btn-primary w-full hover:scale-105 transition-transform duration-200 relative"
            onClick={handleCreateElection}
            title="Create a new election (Admin only)"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
            </svg>
            Create Election
            <span className="absolute -top-1 -right-1 bg-yellow-400 text-yellow-900 text-xs px-1 rounded-full">ADMIN</span>
          </button>
          <button 
            className="btn btn-outline w-full hover:scale-105 transition-transform duration-200 relative"
            onClick={handleViewReports}
            title="View detailed reports (Admin only)"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
            </svg>
            View Reports
            <span className="absolute -top-1 -right-1 bg-yellow-400 text-yellow-900 text-xs px-1 rounded-full">ADMIN</span>
          </button>
          <button 
            className="btn btn-ghost w-full hover:scale-105 transition-transform duration-200"
            onClick={handleSettings}
            title="Access your profile settings"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z" />
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
            </svg>
            Settings
          </button>
        </div>
      </div>

      <div className="card">
        <h3 className="text-h4 text-neutral-900 mb-4">Recent Activity</h3>
        <div className="space-y-3">
          <div className="flex items-center gap-3">
            <div className="w-2 h-2 bg-green-500 rounded-full"></div>
            <div className="flex-1">
              <p className="text-sm font-medium text-neutral-900">New vote cast</p>
              <p className="text-xs text-neutral-500">2 minutes ago</p>
            </div>
          </div>
          <div className="flex items-center gap-3">
            <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
            <div className="flex-1">
              <p className="text-sm font-medium text-neutral-900">Election started</p>
              <p className="text-xs text-neutral-500">1 hour ago</p>
            </div>
          </div>
          <div className="flex items-center gap-3">
            <div className="w-2 h-2 bg-purple-500 rounded-full"></div>
            <div className="flex-1">
              <p className="text-sm font-medium text-neutral-900">New voter registered</p>
              <p className="text-xs text-neutral-500">3 hours ago</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  if (loading) {
    return (
      <DashboardLayout title="Dashboard" subtitle="Welcome to your secure voting dashboard">
        <div className="flex items-center justify-center py-12">
          <div className="spinner mr-3"></div>
          <span className="text-neutral-600">Loading dashboard...</span>
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout 
      title="Dashboard" 
      subtitle="Welcome back! Here's what's happening with your elections."
      stats={stats}
      sidebar={sidebar}
    >
      {/* Charts Section - Compact */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-4 mb-6">
        <Chart
          type="bar"
          title="Vote Distribution"
          subtitle="Percentage breakdown of votes by candidate"
          data={chartData.voterTurnout.data}
          labels={chartData.voterTurnout.labels}
          height={200}
          colors={['#3b82f6', '#22c55e', '#f59e0b']}
        />
        
        <Chart
          type="line"
          title="Votes Cast Over Time"
          subtitle="Total votes cast in each election"
          data={chartData.votesByElection.data}
          labels={chartData.votesByElection.labels}
          height={200}
          colors={['#f97316']}
        />
      </div>

      {/* Elections Section - Compact */}
      <div className="card">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h3 className="text-h4 text-neutral-900">Presidential Election 2024</h3>
            <p className="text-neutral-600 mt-1 text-sm">Manage and monitor your current elections</p>
          </div>
          <button 
            className="btn btn-primary btn-sm"
            onClick={handleCreateElection}
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
            </svg>
            New Election
          </button>
        </div>

        {elections.length === 0 ? (
          <div className="text-center py-12">
            <div className="w-16 h-16 bg-neutral-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <svg className="w-8 h-8 text-neutral-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
            </div>
            <h3 className="text-xl font-semibold text-neutral-600 mb-2">No Elections Available</h3>
            <p className="text-neutral-500">Create your first election to get started.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {elections.map((election, index) => (
              <div 
                key={election._id} 
                className="card hover-lift animate-fadeInUp" 
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="flex items-start justify-between mb-3">
                  <div className="flex-1">
                    <h3 className="text-base font-bold text-neutral-900 mb-1">{election.title}</h3>
                    <p className="text-neutral-600 text-xs mb-2">{election.description}</p>
                  </div>
                  <div className={`px-2 py-1 rounded-full text-xs font-medium ${
                    election.status === 'active' 
                      ? 'bg-green-100 text-green-800' 
                      : election.status === 'completed'
                      ? 'bg-gray-100 text-gray-800'
                      : 'bg-yellow-100 text-yellow-800'
                  }`}>
                    {election.status}
                  </div>
                </div>

                <div className="space-y-2 mb-3">
                  <div className="flex items-center justify-between text-xs">
                    <span className="text-neutral-500">Registered Voters:</span>
                    <span className="font-medium">{election.registeredVoters.toLocaleString()}</span>
                  </div>
                  <div className="flex items-center justify-between text-xs">
                    <span className="text-neutral-500">Total Votes:</span>
                    <span className="font-medium">{election.totalVotes.toLocaleString()}</span>
                  </div>
                  <div className="flex items-center justify-between text-xs">
                    <span className="text-neutral-500">End Date:</span>
                    <span className="font-medium">{new Date(election.endDate).toLocaleDateString()}</span>
                  </div>
                </div>

                <div className="flex space-x-2">
                  {election.status === 'active' && (
                    <button
                      className="btn btn-primary btn-sm flex-1"
                      onClick={() => handleVote(election._id)}
                    >
                      <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                      </svg>
                      Vote Now
                    </button>
                  )}
                  <button
                    className="btn btn-outline btn-sm flex-1"
                    onClick={() => handleViewResults(election._id)}
                  >
                    <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                    </svg>
                    View Results
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Security Features Section - Compact */}
      <div className="card">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h3 className="text-h4 text-neutral-900">Security Features</h3>
            <p className="text-neutral-600 mt-1 text-sm">Advanced security measures protecting your vote</p>
          </div>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <div className="text-center p-4 bg-gradient-to-br from-saffron-50 to-saffron-100 rounded-xl">
            <div className="w-8 h-8 bg-saffron-500 rounded-full flex items-center justify-center mx-auto mb-2">
              <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
              </svg>
            </div>
            <h4 className="text-sm font-semibold text-neutral-900 mb-1">Fingerprint Auth</h4>
            <p className="text-xs text-neutral-600">Biometric verification</p>
          </div>
          
          <div className="text-center p-4 bg-gradient-to-br from-india-green-50 to-india-green-100 rounded-xl">
            <div className="w-8 h-8 bg-india-green-500 rounded-full flex items-center justify-center mx-auto mb-2">
              <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
              </svg>
            </div>
            <h4 className="text-sm font-semibold text-neutral-900 mb-1">Merkle Tree</h4>
            <p className="text-xs text-neutral-600">Vote integrity</p>
          </div>
          
          <div className="text-center p-4 bg-gradient-to-br from-chakra-blue-50 to-chakra-blue-100 rounded-xl">
            <div className="w-8 h-8 bg-chakra-blue-500 rounded-full flex items-center justify-center mx-auto mb-2">
              <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 7a2 2 0 012 2m4 0a6 6 0 01-7.743 5.743L11 17H9v2H7v2H4a1 1 0 01-1-1v-2.586a1 1 0 01.293-.707l5.964-5.964A6 6 0 1121 9z" />
              </svg>
            </div>
            <h4 className="text-sm font-semibold text-neutral-900 mb-1">AES-256</h4>
            <p className="text-xs text-neutral-600">End-to-end encryption</p>
          </div>
          
          <div className="text-center p-4 bg-gradient-to-br from-purple-50 to-purple-100 rounded-xl">
            <div className="w-8 h-8 bg-purple-500 rounded-full flex items-center justify-center mx-auto mb-2">
              <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
              </svg>
            </div>
            <h4 className="text-sm font-semibold text-neutral-900 mb-1">Audit Trail</h4>
            <p className="text-xs text-neutral-600">Complete transparency</p>
          </div>
        </div>
      </div>
    </DashboardLayout>
  );
};

export default Dashboard;
